mapper.select:
select



from
host_component hc
left join (select * from component


        component_name = #{componentName}


        and cluster_id = #{clusterId}


) c
on hc.component_id = c.id

select
, c.service_id, c.component_name, c.display_name, c.category, s.service_name, clus.cluster_name, ht.hostname from host_component hc left join (select * from component
cluster_id = #{clusterId}
and service_id = #{serviceId}
) c on hc.component_id = c.id left join service s on c.service_id = s.id left join cluster clus on c.cluster_id = clus.id left join host ht on hc.host_id = ht.id

select



from
host_component hc
left join (select * from host


        hostname = #{hostname}


        and cluster_id = #{clusterId}


) h
on hc.host_id = h.id
left join (select * from component


        component_name = #{componentName}


        and cluster_id = #{clusterId}


) c
on hc.component_id = c.id

select
, c.service_id, c.component_name, c.display_name, c.category, s.service_name, clus.cluster_name, ht.hostname from host_component hc left join component c on hc.component_id = c.id left join service s on c.service_id = s.id left join cluster clus on c.cluster_id = clus.id left join (select * from host
cluster_id = #{clusterId}
and id = #{hostId}
) ht on hc.host_id = ht.id

select
, c.service_id, c.component_name, c.display_name, c.category, s.service_name, clus.cluster_name, ht.hostname from host_component hc left join (select * from component
cluster_id = #{clusterId}
) c on hc.component_id = c.id left join service s on c.service_id = s.id left join cluster clus on c.cluster_id = clus.id left join host ht on hc.host_id = ht.id
