mapper.select:
select



from
host_component hc
inner join (select * from component


        component_name = #{componentName}


        and cluster_id = #{clusterId}


) c
on hc.component_id = c.id

select
, c.service_id, c.component_name, c.display_name, c.category, s.service_name, clus.name as cluster_name, ht.hostname from host_component hc inner join (select * from component
cluster_id = #{clusterId}
and service_id = #{serviceId}
) c on hc.component_id = c.id inner join service s on c.service_id = s.id inner join cluster clus on c.cluster_id = clus.id inner join host ht on hc.host_id = ht.id

select



from
host_component hc
inner join (select * from host


        hostname = #{hostname}


        and cluster_id = #{clusterId}


) h
on hc.host_id = h.id
inner join (select * from component


        component_name = #{componentName}


) c
on hc.component_id = c.id and h.cluster_id = c.cluster_id
limit 1

select



, c.component_name, h.hostname
from
host_component hc
inner join (select * from host


        hostname in

            #{hostname}



        and cluster_id = #{clusterId}


) h
on hc.host_id = h.id
inner join (select * from component


        component_name = #{componentName}


) c
on hc.component_id = c.id and h.cluster_id = c.cluster_id

select
, c.service_id, c.component_name, c.display_name, c.category, s.service_name, clus.name as cluster_name, ht.hostname from host_component hc inner join component c on hc.component_id = c.id inner join service s on c.service_id = s.id inner join cluster clus on c.cluster_id = clus.id inner join (select * from host
cluster_id = #{clusterId}
and id = #{hostId}
) ht on hc.host_id = ht.id

select
, c.service_id, c.component_name, c.display_name, c.category, s.service_name, clus.name as cluster_name, ht.hostname from host_component hc inner join (select * from component
cluster_id = #{clusterId}
) c on hc.component_id = c.id inner join service s on c.service_id = s.id inner join cluster clus on c.cluster_id = clus.id inner join host ht on hc.host_id = ht.id

select count(*)
from host_component
where host_id = #{hostId}
