# Java export JAVA_HOME=${java_home} export PATH=$PATH:${JAVA_HOME}/bin
# Atlas directories export ATLAS_LOG_DIR=${atlas_log_dir} export ATLAS_PID_DIR=${atlas_pid_dir} export ATLAS_PORT=${atlas_port}
# External components export HBASE_CONF_DIR=${hbase_conf_dir} export HADOOP_HOME=${hadoop_home}
# Local component flags export MANAGE_LOCAL_HBASE=${manage_local_hbase} export MANAGE_LOCAL_SOLR=${manage_local_solr} export MANAGE_EMBEDDED_CASSANDRA=${manage_embedded_cassandra} export MANAGE_LOCAL_ELASTICSEARCH=${manage_local_elasticsearch}
