property.value:
# The heap size of the jvm, and jvm args stared by hive shell script can be controlled via: if [ "$SERVICE" = "metastore" ]; then
export HADOOP_HEAPSIZE=${hive_metastore_heapsize} # Setting for HiveMetastore export HADOOP_OPTS="$HADOOP_OPTS -Xloggc:${hive_log_dir}/hivemetastore-gc-%t.log -XX:+PrintGCDetails -XX:+PrintGCTimeStamps -XX:+PrintGCCause -XX:+UseGCLogFileRotation -XX:NumberOfGCLogFiles=10 -XX:GCLogFileSize=10M -XX:+HeapDumpOnOutOfMemoryError -XX:HeapDumpPath=${hive_log_dir}/hms_heapdump.hprof -Dhive.log.dir=${hive_log_dir} -Dhive.log.file=hivemetastore.log"
fi
if [ "$SERVICE" = "hiveserver2" ]; then
export HADOOP_HEAPSIZE=${hive_heapsize} # Setting for HiveServer2 and Client export HADOOP_OPTS="$HADOOP_OPTS -Xloggc:${hive_log_dir}/hiveserver2-gc-%t.log -XX:+PrintGCDetails -XX:+PrintGCTimeStamps -XX:+PrintGCCause -XX:+UseGCLogFileRotation -XX:NumberOfGCLogFiles=10 -XX:GCLogFileSize=10M -XX:+HeapDumpOnOutOfMemoryError -XX:HeapDumpPath=${hive_log_dir}/hs2_heapdump.hprof -Dhive.log.dir=${hive_log_dir} -Dhive.log.file=hiveserver2.log"
fi
<#noparse> export HADOOP_CLIENT_OPTS="$HADOOP_CLIENT_OPTS -Xms${HADOOP_HEAPSIZE}m -Xmx${HADOOP_HEAPSIZE}m" </#noparse> export HADOOP_CLIENT_OPTS="$HADOOP_CLIENT_OPTS -XX:+HeapDumpOnOutOfMemoryError -XX:HeapDumpPath=${heap_dump_path}"
# Larger heap size may be required when running queries over large number of files or partitions. # By default hive shell scripts use a heap size of 256 (MB). Larger heap size would also be # appropriate for hive server (hwi etc).
# Set HADOOP_HOME to point to a specific hadoop install directory HADOOP_HOME=${hadoop_home}
export HIVE_HOME=${hive_home}
# Hive Configuration Directory can be controlled by: export HIVE_CONF_DIR=${hive_conf_dir}
# Folder containing extra libraries required for hive compilation/execution can be controlled by: if [ "$HIVE_AUX_JARS_PATH" != "" ]; then export HIVE_AUX_JARS_PATH=$HIVE_AUX_JARS_PATH fi
export METASTORE_PORT=${hive_metastore_port}
